  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isAddFilmPage) {
      if (
        validation(
          state.title,
          state.description,
          state.posterFilm,
          state.bannerFilm,
          state.trailerURL,
          state.filmURL,
          state.actor,
          state.titleSearch,
          state.titleSlug,
        ) &&
        state.genre.length > 0 &&
        !msg
      ) {
        progressData();
      } else {
        setValidate('Vui lòng điền đúng tất cả ô trống');
      }
    } else if (!msg) {
      progressData();
    }
  };

  // const toggleSwitchMode = () => {
  //   setState({
  //     ...state,
  //     switchMode: !state.switchMode,
  //     posterFilm: '',
  //     bannerFilm: '',
  //   });
