                      ...state,
                      titleSlug: e.target.value,
                    });
                    const checkSlug = isAddFilmPage
                      ? await checkSlugApi(e.target.value)
                      : e.target.value === slug
                      ? { data: { msg: '' } }
                      : await checkSlugApi(e.target.value);
                    setMsg(checkSlug.data.msg);
                  }}
                  value={state.titleSlug}
