                      ...state,
                      titleSlug: getSlug(state.title),
                    });
                    const checkSlug = isAddFilmPage
                      ? await checkSlugApi(getSlug(state.title))
                      : getSlug(state.title) === slug
                      ? { data: { msg: '' } }
                      : await checkSlugApi(getSlug(state.title));
                    setMsg(checkSlug.data.msg);
