              <InputImageFile
                id='posterFilm'
                value={state.posterFilm}
                placeholder='Chọn Poster'
                width='w-14rem'
                setState={(value) => {
                  console.log("value::", value);
                  setState((newState) => ({
                    ...newState,
                    posterFilm: `${value?.path}` ?? '',
                  }));
                }}
                styleContainer='mr-6'
                styleLabel='ml-6'
              />
              <InputImageFile
                id='bannerFilm'
                value={state.bannerFilm}
                placeholder='Chọn Banner'
                width='w-26rem'
                setState={(value) => {
                  setState({
                    ...state,
