// eslint-disable-next-line import/no-extraneous-dependencies
import { toast } from "react-toastify";

export default toastMessage = (type = 'sucess', message = '') => {
    toast[type](message, {
        position: "top-right",
        autoClose: 5000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "dark",
      });
}
